bot.on('message', message => {
  let cont = message.content.slice(prefix.length).split(" ");
  const args = cont.slice(1);
  if (message.content.startsWith(prefix + "Plaine combat")) {
    let joueurs = args.slice(2).join(" : ");
    if (talkedRecently.has(message.author.id + 1000)) {
      const embed = new Discord.RichEmbed()
        .setAuthor(message.author.username, message.author.avatarURL)
        .setFooter("『SAO Community [RP]』©", "http://www.copyrightfrance.com/images/copyright.png")
        .setColor(3447003)
        .addField("Cooldown :", " Vous devrez attendre 10 minutes avant de pouvoir refaire ceci !")
        .setImage("https://vignette.wikia.nocookie.net/swordartonline/images/4/43/AnimeJohnnyBlack.png/revision/latest?cb=20140328034625")
        .setTimestamp()
      message.channel.send({ embed })
    } else {
      let test = 0;
      let renard = 0;
      let sanglier = 0;
      let chien = 0;
      let bandit = 0;
      let lapin = 0;
      let roll = 0;
      do {
        roll = Math.floor(100 * Math.random() + 1)
        if (roll <= 75) {
          test = test + 1 + renard;
          if (test <= (5 + 6 * (joueurs - 1))) {
            renard = renard + 1;
          } else break;
        }
        roll = Math.floor(100 * Math.random() + 1)
        if (roll <= 60) {
          test = test + 2 + sanglier;
          if (test <= (5 + 6 * (joueurs - 1))) {
            sanglier = sanglier + 1;
          } else break;
        }
        roll = Math.floor(100 * Math.random() + 1)
        if (roll <= 40) {
          test = test + 3 + bandit;
          if (test <= (5 + 6 * (joueurs - 1))) {
            bandit = bandit + 1;
          } else break;
        }
        roll = Math.floor(100 * Math.random() + 1)
        if (roll <= 30) {
          test = test + 3 + (2 * lapin);
          if (test <= (5 + 6 * (joueurs - 1))) {
            lapin = lapin + 1;
          } else break;
        }
        roll = Math.floor(100 * Math.random() + 1)
        if (roll <= 75) {
          test = test + 2 + chien;
          if (test <= (5 + 6 * (joueurs - 1))) {
            chien = chien + 1;
          } else break;
        }
      } while (test <= (5 + 6 * (joueurs - 1)));
      const embed = new Discord.RichEmbed()
        .setAuthor(message.author.username, message.author.avatarURL)
        .setFooter("『SAO Community [RP]』©", "http://www.copyrightfrance.com/images/copyright.png")
        .setColor(3447003)
        .addField(":sunrise_over_mountains: Plaines :", ":sunrise_over_mountains: En marchant dans les plaines, vous rencontrez les ennemis suivants\n\n" +
          ":crossed_swords: Renard(s) | 20HP : " + renard + "\n" +
          ":crossed_swords: Sanglier(s) | 30HP : " + sanglier + "\n" +
          ":crossed_swords: Chien(s) | 15HP : " + chien + "\n" +
          ":crossed_swords: Bandit(s) débutant(s) | 40HP : " + bandit + "\n" +
          ":crossed_swords: Lapin(s) géant(s) | 70HP : " + lapin)
        .setImage("https://i.pinimg.com/originals/74/ef/20/74ef206acce786bab2081e1fae7aa94e.jpg")
        .setTimestamp()
      message.channel.send({ embed })
    }
    talkedRecently.add(message.author.id + 1000);
    setTimeout(() => {
      talkedRecently.delete(message.author.id + 1000);
    }, 600000);
  }
})

